import React from 'react';
import { Link } from 'react-router-dom';
import { FaLock, FaMoneyBillAlt, FaChartLine } from 'react-icons/fa';

const PaymentPortalHomePage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-red-600 via-black to-white flex flex-col items-center justify-center p-6 relative overflow-hidden">
      {/* Floating Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-10 left-10 w-80 h-80 bg-red-500 opacity-30 rounded-full filter blur-3xl animate-float"></div>
        <div className="absolute bottom-10 right-10 w-80 h-80 bg-black opacity-20 rounded-full filter blur-3xl animate-float-slow"></div>
        <div className="absolute bottom-40 left-40 w-60 h-60 bg-red-700 opacity-25 rounded-full filter blur-3xl animate-float-reverse"></div>
      </div>

      {/* Welcome Section */}
      <div className="relative z-10 bg-white/90 backdrop-blur-md p-12 rounded-3xl shadow-xl text-center max-w-lg mx-auto transform transition-transform duration-500 hover:scale-105 hover:shadow-2xl">
        <h1 className="text-5xl font-extrabold text-red-700 mb-6 tracking-tight leading-tight">
          Welcome to Our <br /> Payment Portal
        </h1>
        <p className="text-lg text-gray-800 mb-8 leading-relaxed">
          Securely manage your international payments and get deeper financial insights.
        </p>
        <div className="grid grid-cols-3 gap-8 text-center">
          <div>
            <FaLock className="text-red-500 text-4xl mb-2" />
            <h3 className="text-lg font-medium text-gray-800">Secure Payments</h3>
          </div>
          <div>
            <FaMoneyBillAlt className="text-black text-4xl mb-2" />
            <h3 className="text-lg font-medium text-gray-800">Manage Payments</h3>
          </div>
          <div>
            <FaChartLine className="text-red-800 text-4xl mb-2" />
            <h3 className="text-lg font-medium text-gray-800">Financial Insights</h3>
          </div>
        </div>
        <Link
          to="/login"
          className="inline-block bg-gradient-to-r from-red-500 to-black text-white text-lg font-semibold py-3 px-8 rounded-full shadow-md transform transition-transform duration-300 hover:scale-110 hover:shadow-lg mt-8"
        >
          Login to Your Account
        </Link>
      </div>

      {/* Credits Section */}
      <footer className="mt-16 text-gray-500 text-sm text-center relative z-10 font-medium">
        Made with ❤️ by <span className="text-red-600">Kiatlyn</span>
      </footer>

      {/* Custom Animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(-10px); }
          50% { transform: translateY(10px); }
        }
        @keyframes float-slow {
          0%, 100% { transform: translateY(5px); }
          50% { transform: translateY(-5px); }
        }
        @keyframes float-reverse {
          0%, 100% { transform: translateY(10px); }
          50% { transform: translateY(-10px); }
        }
        .animate-float { animation: float 6s ease-in-out infinite; }
        .animate-float-slow { animation: float-slow 10s ease-in-out infinite; }
        .animate-float-reverse { animation: float-reverse 8s ease-in-out infinite; }
      `}</style>
    </div>
  );
};

export default Home;
