// src/components/UserHome.js

import React, { useEffect, useState, useCallback } from 'react';
import {
  Home as HomeIcon,
  Send,
  Clock,
  PieChart,
  FileText,
  DollarSign,
  Menu,
  X,
} from 'lucide-react';
import PaymentForm from './PaymentForm';
import TransferFunds from './TransferFunds';
import RecentTransactions from './RecentTransactions';
import FinancialInsights from './FinancialInsights';
import Statements from './Statements';
import { getBalanceAndTransactions } from '../api';

const UserHome = ({ token }) => {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [selectedTab, setSelectedTab] = useState('overview');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const fetchUserBalanceAndTransactions = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await getBalanceAndTransactions(token);
      setBalance(response.data.balance);
      setTransactions(response.data.transactions);
    } catch (error) {
      console.error('Failed to fetch balance and transactions:', error);
      setError('Could not load data. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchUserBalanceAndTransactions();
  }, [fetchUserBalanceAndTransactions]);

  const tabs = [
    { id: 'overview', label: 'Overview', icon: <HomeIcon size={20} /> },
    { id: 'makePayment', label: 'Make a Payment', icon: <DollarSign size={20} /> },
    { id: 'transferFunds', label: 'Transfer Funds', icon: <Send size={20} /> },
    { id: 'recentTransactions', label: 'Recent Transactions', icon: <Clock size={20} /> },
    { id: 'financialInsights', label: 'Financial Insights', icon: <PieChart size={20} /> },
    { id: 'statements', label: 'View Statements', icon: <FileText size={20} /> },
  ];

  return (
    <div className="min-h-screen flex bg-black text-white">
      {/* Sidebar */}
      <div
        className={`fixed inset-y-0 left-0 transform ${
          sidebarOpen ? 'translate-x-0' : '-translate-x-full'
        } transition-transform duration-300 ease-in-out z-50 bg-gray-900 w-64 border-r border-gray-800`}
      >
        <div className="flex items-center justify-between p-4 border-b border-gray-800">
          <h2 className="text-2xl font-bold">Dashboard</h2>
          <button
            onClick={() => setSidebarOpen(false)}
            className="text-gray-400 hover:text-white focus:outline-none"
          >
            <X size={24} />
          </button>
        </div>
        <nav className="mt-4">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => {
                setSelectedTab(tab.id);
                setSidebarOpen(false);
              }}
              className={`flex items-center w-full px-4 py-3 text-left hover:bg-red-600 transition-colors duration-200 ${
                selectedTab === tab.id ? 'bg-red-600' : 'bg-gray-900'
              }`}
            >
              <span className="mr-3">{tab.icon}</span>
              {tab.label}
            </button>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Mobile Header */}
        <div className="flex items-center justify-between p-4 bg-gray-900 border-b border-gray-800 md:hidden">
          <h2 className="text-2xl font-bold">Dashboard</h2>
          <button
            onClick={() => setSidebarOpen(true)}
            className="text-gray-400 hover:text-white focus:outline-none"
          >
            <Menu size={24} />
          </button>
        </div>

        {/* Header Section */}
        <div className="hidden md:flex items-center justify-between p-6 bg-gray-900 border-b border-gray-800">
          <h2 className="text-3xl font-bold">Welcome Back!</h2>
          <div className="flex items-center space-x-4">
            <div className="text-right">
              <p className="text-gray-400 text-sm">Current Balance</p>
              <p className="text-2xl font-bold text-white">
                R{balance.toFixed(2)}
              </p>
            </div>
            <div className="bg-red-600 p-3 rounded-full">
              <DollarSign size={24} />
            </div>
          </div>
        </div>

        {/* Content Section */}
        <div className="flex-1 p-6 overflow-auto">
          {loading ? (
            <div className="flex items-center justify-center h-full">
              <p className="text-gray-500">Loading data...</p>
            </div>
          ) : error ? (
            <div className="flex items-center justify-center h-full">
              <p className="text-red-500">{error}</p>
            </div>
          ) : (
            <>
              {/* Overview Section */}
              {selectedTab === 'overview' && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {/* Recent Transactions */}
                  <div className="bg-gray-900 p-6 rounded-lg shadow-lg">
                    <h3 className="text-xl font-semibold mb-4">Recent Transactions</h3>
                    <RecentTransactions transactions={transactions.slice(0, 5)} />
                  </div>
                  {/* Financial Insights */}
                  <div className="bg-gray-900 p-6 rounded-lg shadow-lg">
                    <FinancialInsights transactions={transactions} />
                  </div>
                </div>
              )}
              {/* Other Sections */}
              {selectedTab === 'makePayment' && (
                <PaymentForm
                  token={token}
                  onTransactionUpdate={fetchUserBalanceAndTransactions}
                />
              )}
              {selectedTab === 'transferFunds' && (
                <TransferFunds
                  balance={balance}
                  token={token}
                  onTransactionUpdate={fetchUserBalanceAndTransactions}
                />
              )}
              {selectedTab === 'recentTransactions' && (
                <div className="bg-gray-900 p-6 rounded-lg shadow-lg">
                  <h3 className="text-2xl font-semibold mb-4">Recent Transactions</h3>
                  <RecentTransactions transactions={transactions} />
                </div>
              )}
              {selectedTab === 'financialInsights' && (
                <FinancialInsights transactions={transactions} />
              )}
              {selectedTab === 'statements' && (
                <div className="bg-gray-900 p-6 rounded-lg shadow-lg">
                  <Statements transactions={transactions} />
                </div>
              )}
            </>
          )}
        </div>
      </div>

      {/* Overlay */}
      {sidebarOpen && (
        <div
          className="fixed inset-0 bg-black opacity-50 z-40"
          onClick={() => setSidebarOpen(false)}
        ></div>
      )}
    </div>
  );
};

export default UserHome;
